import React from "react"

import "./css/UploadPopup.css"
import UploadChoicePopup from "./UploadChoicePopup"

export default function UploadPopup() {

    const [isChoiceShown, setIsChoiceShown] = React.useState(false);

    function uploadPicHandler() {
        setIsChoiceShown(prevState => !prevState);
    }

    return (<div className="popup-container">
                <div className="choice-popup">
                    <div className="choice-1 choice-item" uploadsPic={uploadPicHandler}>Upload Photo</div>
                    <div className="choice-2 choice-item">Import from Web</div>
                    {isChoiceShown && <UploadChoicePopup />}
                </div>
                <div className="popup-triangle"></div>
            </div>)
}
