import React from "react"
import "./css/Closet.css"
import {icons} from "./Icons"


import CategoryList from "./CategoryList"
import Footer from "./Footer"
import UploadPopup from "./UploadPopup"
import UploadChoicePopup from "./UploadChoicePopup"

export default function Closet() {
    const [isPopupShown, setIsPopupShown] = React.useState(false);
    const [isChoiceShown, setIsChoiceShown] = React.useState(false);

    function uploadPicHandler() {
        setIsChoiceShown(prevState => !prevState);
    }

    function bringUploadPic() {
        setIsPopupShown(prevStatus => !prevStatus);
        console.log("I am clicked")
    }   


    return (
        <div className="page--container">
            <div className="closet-header-box">
                <div className="category-title">
                    <img className="side-bar--icons" src={icons[0].path}/>
                    Category
                </div>
                <div className="header-button-wrapper">
                    <div className="header--button">Edit</div>
                    <div className="box-banner-name">Closet</div>
                    <div className="header--button inactive-button">+ Look</div>
                </div>
            </div>
            <div className="closet-body-box">
                <div className="categories-box">
                    <CategoryList />
                    <Footer popupHandler={bringUploadPic}/>
                    {isPopupShown && <UploadPopup className={isChoiceShown && "disabled-popup"}/>}
                    {/* {isChoiceShown && <UploadChoicePopup bringChoices={uploadPicHandler}/>} */}
                </div>
            </div>
        </div>
    )
}
